using Generator3.Renderer.Native;

namespace Generator3.Generation.Class.Fundamental
{
    public class NativeInstanceStructTemplate : Template<NativeInstanceStructModel>
    {
        public string Render(NativeInstanceStructModel model)
        {
            return $@"
using System;
using GObject;
using System.Runtime.InteropServices;

#nullable enable

namespace { model.NamespaceName }
{{
    // AUTOGENERATED FILE - DO NOT MODIFY
    public partial class { model.Name }
    {{
        public partial class Instance
        {{
            public partial struct Struct
            {{
                {model.Fields.Render()}
            }}
        }}
    }}
}}";
        }
    }
}
