using Generator3.Renderer.Public;

namespace Generator3.Generation.Class.Standard
{
    public class PublicConstructorsTemplate : Template<PublicConstructorsModel>
    {
        public string Render(PublicConstructorsModel model)
        {
            return $@"
using System;
using System.Linq;
using GObject;
using System.Runtime.InteropServices;

#nullable enable

namespace { model.NamespaceName }
{{
    // AUTOGENERATED FILE - DO NOT MODIFY

    public partial class { model.Name }
    {{
        {model.Constructors.Render()}
    }}
}}";
        }
    }
}
